import React, { useState, useEffect } from 'react';
import Form from './components/Form';
import List from './components/List';
import { getItems, deleteItem } from './api'; // üëà adicione deleteItem

const App = () => {
  const [itemToUpdate, setItemToUpdate] = useState(null);
  const [items, setItems] = useState([]);

  const refreshList = async () => {
    const itemsFromServer = await getItems();
    setItems(itemsFromServer);
    setItemToUpdate(null);
  };

  useEffect(() => {
    refreshList();
  }, []);

  // üëá Fun√ß√£o para excluir item
  const handleDelete = async (id) => {
    try {
      await deleteItem(id);
      refreshList(); // Atualiza a lista ap√≥s exclus√£o
    } catch (error) {
      console.error('Erro ao excluir item:', error);
    }
  };

  return (
    <div>
      <h1>CRUD App</h1>
      <Form itemToUpdate={itemToUpdate} refreshList={refreshList} />
      <List
        items={items}
        setItemToUpdate={setItemToUpdate}
        onDelete={handleDelete} // üëà Passa fun√ß√£o de exclus√£o
      />
    </div>
  );
};

export default App;
