import { BrowserRouter, Route, Routes } from 'react-router-dom'
import React, { useState } from 'react';
import Form from './components/Form';
import List from './components/List';

const App = () => {
    const [itemToUpdate, setItemToUpdate] = useState(null);

    const refreshList = () => {
        setItemToUpdate(null);
    };

    return (
        <BrowserRouter>
                <div>
            <h1>CRUD App</h1>
            <Form itemToUpdate={itemToUpdate} setItemToUpdate={setItemToUpdate} refreshList={refreshList} />
            <List setItemToUpdate={setItemToUpdate} />
        </div>
            <div className="App">
                <h1>Cadastro de Itens</h1>
                <Routes>
                    <Route path="/" element={<List />} />
                    <Route path="/form" element={<Form itemToUpdate={itemToUpdate} refreshList={refreshList} />} />
                </Routes>
            </div>
        </BrowserRouter>
    );
};

export default App;
