import React, { useState } from 'react';
import Form from './components/Form';
import List from './components/List';

const App = () => {
    const [itemToUpdate, setItemToUpdate] = useState(null);

    const refreshList = () => {
        setItemToUpdate(null); // Limpa o item a ser editado
    };

    return (
        <div>
            <h1>CRUD App</h1>
            {/* Verifique se há um item para atualizar. Caso contrário, não renderize o Form */}
            {itemToUpdate ? (
                <Form itemToUpdate={itemToUpdate} refreshList={refreshList} />
            ) : (
                <Form refreshList={refreshList} />
            )}
            <List setItemToUpdate={setItemToUpdate} />
        </div>
    );
};

export default App;
